mapper.select:
select 

from acl_user
where user_id = #{userId,jdbcType=INTEGER}
  and openid = #{openid,jdbcType=VARCHAR}
  and login_name = #{loginName,jdbcType=VARCHAR}

mapper.delete:
delete from acl_user
where user_id = #{userId,jdbcType=INTEGER}
  and openid = #{openid,jdbcType=VARCHAR}
  and login_name = #{loginName,jdbcType=VARCHAR}
